create database Web_banson

create table Categogies (
	id INT primary key,
	name VARCHAR(50),
	[description] NVARCHAR(600)
);

drop table Product

select*from Product

create table Product (
	id INT identity(1,1) primary key,
	name VARCHAR(50),
	category_id INT,
	quantity INT,
	price INT,
	description NVARCHAR(500),
	imagine VARCHAR(max),
	imagine2 VARCHAR(max),
	imagine3 VARCHAR(max),
	created_date DATE,
	FOREIGN KEY (category_id) REFERENCES Categogies(id)
);


drop table Account
create table Account (
	id INT,
	username VARCHAR(50) primary key,
	password VARCHAR(50),
	displayname VARCHAR(50),
	email VARCHAR(50),
	numberphone VARCHAR(50),
	address VARCHAR(50),
	status int
);
